import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faBars, faSearch, faTh, faUser, faComments, faChartPie, faFolder, faShoppingCart, faHeart, faCog, faSignOutAlt } from '@fortawesome/free-solid-svg-icons';
import './Menu.css';

const Menu = () => {
  const [isActive, setIsActive] = useState(false);

  const toggleSidebar = () => {
    setIsActive(!isActive);
  };

  return (
    <div className={`sidebar ${isActive ? 'active' : ''}`}>
      <div className="logo_content">
        <div className="logo">
          <FontAwesomeIcon icon={faBars} style={{ fontSize: '30px' }} />
          <div className="logoname" style={{ marginLeft: '5px' }}>Brand Name</div>
        </div>
        <FontAwesomeIcon icon={faBars} onClick={toggleSidebar} style={{ fontSize: '25px' }} />
      </div>
      <ul className="nav_list">
        <li>
          <FontAwesomeIcon icon={faSearch} />
          <input type="text" placeholder="Search..." />
          <span className="tooltip">Search</span>
        </li>
        <li>
          <a href="#">
            <FontAwesomeIcon icon={faTh} />
            <span className="link_names">Dashboard</span>
          </a>
          <span className="tooltip">Dashboard</span>
        </li>
        {/* Add other list items here */}
      </ul>
      <div className="profile_content">
        <div className="profile">
          <div className="profile_details">
            <img src="https://vz.cnwimg.com/wp-content/uploads/2014/01/alex.jpg?x86007" alt="" />
            <div className="name_job">
              <div className="name">Name</div>
              <div className="job">Role</div>
            </div>
          </div>
          <FontAwesomeIcon icon={faSignOutAlt} id="log_out" />
        </div>
      </div>
    </div>
  );
};

export default Menu;
