import React, { useState } from 'react';
import './Menu.css';

const Menu = () => {
  const [isActive, setIsActive] = useState(false);

  const toggleSidebar = () => {
    setIsActive(!isActive);
  };

  return (
    <div className={`sidebar ${isActive ? 'active' : ''}`}>
      <div className="logo_content">
        <div className="logo">
          <i className='bx bxl-c-plus-plus' style={{ fontSize: '30px' }}></i>
          <div className="logoname" style={{ marginLeft: '5px' }}>Brand Name</div>
        </div>
        <i className='bx bx-menu-alt-right' onClick={toggleSidebar} style={{ fontSize: '25px' }}></i>
      </div>
      <ul className="nav_list">
        <li>
          <i className='bx bx-search'></i>
          <input type="text" placeholder="Search..." />
          <span className="tooltip">Search</span>
        </li>
        <li>
          <a href="#">
            <i className='bx bx-grid-alt'></i>
            <span className="link_names">Dashboard</span>
          </a>
          <span className="tooltip">Dashboard</span>
        </li>
        {/* Add other list items here */}
      </ul>
      <div className="profile_content">
        <div className="profile">
          <div className="profile_details">
            <img src="https://vz.cnwimg.com/wp-content/uploads/2014/01/alex.jpg?x86007" alt="" />
            <div className="name_job">
              <div className="name">Name</div>
              <div className="job">Role</div>
            </div>
          </div>
          <i className='bx bx-log-out' id="log_out"></i>
        </div>
      </div>
    </div>
  );
};

export default Menu;
