import React, { useState } from 'react';
import './Menu.css';

const Menu = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggleMenu = () => {
    setIsOpen(!isOpen);
  };

  const navItems = document.querySelectorAll(".nav-item");

navItems.forEach((navItem, i) => {
	navItem.addEventListener("click", () => {
		navItems.forEach((item, j) => {
			item.className = "nav-item";
		});
		navItem.className = "nav-item active";
	});
});


  return (
    <main>
      <nav className={`main-menu ${isOpen ? 'open' : ''}`}>
        <h1>Fitness App</h1>
        <img className="logo" src="https://github.com/ecemgo/mini-samples-great-tricks/assets/13468728/4cfdcb5a-0137-4457-8be1-6e7bd1f29ebb" alt="" />
        <ul>
          <li className="nav-item active">
            <b></b>
            <b></b>
            <a href="#">
              <i className="fa fa-house nav-icon"></i>
              <span className="nav-text">Home</span>
            </a>
          </li>
          {/* Add other menu items here */}
        </ul>
        <div className="menu-toggle" onClick={toggleMenu}>
          <span></span>
          <span></span>
          <span></span>
        </div>
      </nav>
      <section className="content">
        <div className="left-content">
          <div className="activities">
            <h1>Popular Activities</h1>
            <div className="activity-container">
              {/* Add activity containers here */}
            </div>
          </div>
          <div className="left-bottom">
            <div className="weekly-schedule">
              {/* Add weekly schedule content here */}
            </div>
            <div className="personal-bests">
              {/* Add personal bests content here */}
            </div>
          </div>
        </div>
        <div className="right-content">
          <div className="user-info">
            {/* Add user info content here */}
          </div>
          <div className="active-calories">
            {/* Add active calories content here */}
          </div>
          <div className="mobile-personal-bests">
            {/* Add mobile personal bests content here */}
          </div>
          <div className="friends-activity">
            {/* Add friends activity content here */}
          </div>
        </div>
      </section>
    </main>
  );
};

export default Menu;
